"use strict";
var Data_Either = require("../Data.Either");
var Data_String_Regex = require("../Data.String.Regex");
var Data_String_Regex_Flags = require("../Data.String.Regex.Flags");
var Partial_Unsafe = require("../Partial.Unsafe");

// | Constructs a `Regex` from a pattern string and flags. Fails with

// | an exception if the pattern contains a syntax error.
var unsafeRegex = function (s) {
    return function (f) {
        return Data_Either.fromRight()(Data_String_Regex.regex(s)(f));
    };
};
module.exports = {
    unsafeRegex: unsafeRegex
};
